        // Shared Tags
        private static string CURRENT_DATE_TAG = "«CURRENT_DATE»";
        private static string FIRST_NAME_TAG = "«First_Name»";
        private static string LAST_NAME_TAG = "«Last_Name»";
        private static string STREET_ADDRESS_TAG = "«Street_Address»";
        private static string APARMENT_TAG = "«Apartment»";
        private static string CITY_TOWN_TAG = "«CityTown»";
        private static string STATE_PROVINCE_TAG = "«StateProvince»";
        private static string ZIP_POSTAL_TAG = "«ZipPostal_Code»";
        private static string SALUTATION_TAG = "«Salutation_Greeting_Dear_So_and_So»:";
        private static string DONATED_ON_TAG = "«Donated_On»";

        // Specific to Food dontaions
        private static string WEIGHT_TAG = "«Weight_lbs»";

        // Specific to Monetary donations
        private static string AMOUNT_TAG = "«M_Amount»";



        public static string getHTML(MonetaryDonor donor)
        {
            StringBuilder html = new StringBuilder(getMonetaryHTMLTemplate());

            html.Replace(CURRENT_DATE_TAG, DateTime.Now.ToString("MMMM dd, yyyy"));
            html.Replace(FIRST_NAME_TAG, donor.firstName);
            html.Replace(LAST_NAME_TAG, donor.lastName);
            html.Replace(STREET_ADDRESS_TAG, donor.streetAddress);
            html.Replace(APARMENT_TAG, donor.apartment);
            html.Replace(CITY_TOWN_TAG, donor.cityTown);
            html.Replace(STATE_PROVINCE_TAG, donor.stateProvince);
            html.Replace(ZIP_POSTAL_TAG, donor.zipPostalCode);
            html.Replace(SALUTATION_TAG, donor.salutationGreeting);
            html.Replace(AMOUNT_TAG, donor.amount);                     // Unique
            html.Replace(DONATED_ON_TAG, donor.donatedOn);

            return html.ToString();
        }

        public static string getHTML(FoodDonor donor)
        {
            StringBuilder html = new StringBuilder(getFoodHTMLTemplate());

            html.Replace(CURRENT_DATE_TAG, DateTime.Now.ToString("MMMM dd, yyyy"));
            html.Replace(FIRST_NAME_TAG, donor.firstName);
            html.Replace(LAST_NAME_TAG, donor.lastName);
            html.Replace(STREET_ADDRESS_TAG, donor.streetAddress);
            html.Replace(APARMENT_TAG, donor.apartment);
            html.Replace(CITY_TOWN_TAG, donor.cityTown);
            html.Replace(STATE_PROVINCE_TAG, donor.stateProvince);
            html.Replace(ZIP_POSTAL_TAG, donor.zipPostalCode);
            html.Replace(SALUTATION_TAG, donor.salutationGreeting);
            html.Replace(WEIGHT_TAG, donor.weight);                     // Unique
            html.Replace(DONATED_ON_TAG, donor.donatedOn);

            return html.ToString();
        }

